mapper.select:
SELECT user_id, email, password_hash, must_reset_password, created_at, updated_at
FROM user_credentials
WHERE user_id = #{id};

SELECT user_id, email, password_hash, must_reset_password, created_at, updated_at
FROM user_credentials
WHERE email = #{email};

SELECT user_id, email, password_hash, must_reset_password, created_at, updated_at
FROM user_credentials
WHERE user_id = #{id}
FOR UPDATE;

mapper.update:
UPDATE user_credentials
SET
email = #{email},
password_hash = #{passwordHash},
must_reset_password = #{mustResetPassword}
WHERE user_id = #{userId};
