mapper.select:
SELECT id, payment_id, stripe_payment_intent_id, stripe_charge_id, stripe_payment_status,
receipt_url, amount, currency, stripe_created_at, updated_at, created_at
FROM payment_transactions;

SELECT id, payment_id, stripe_payment_intent_id, stripe_charge_id, stripe_payment_status,
receipt_url, amount, currency, stripe_created_at, updated_at, created_at
FROM payment_transactions
WHERE id = #{id}
FOR UPDATE;
