SELECT id, payment_id, stripe_session_id, stripe_payment_status,
amount, session_url, currency, updated_at, created_at
FROM payment_transactions;

SELECT id, payment_id, stripe_session_id, stripe_payment_status,
amount, session_url, currency, updated_at, created_at
FROM payment_transactions
WHERE id = #{id}
FOR UPDATE;
