mapper.select:
SELECT product_id, product_category, product_brand, product_name, product_description,
recommended_price, product_status, updated_at, created_at
FROM products;

SELECT product_id, product_category, product_brand, product_name, product_description,
recommended_price, product_status, updated_at, created_at
FROM products
WHERE product_id = #{productId}
FOR UPDATE;
